mapper.insert:
insert into param (id, domain_name, task_name,
  param_name, acquire_type, group_num, 
  acquire_content, version, enable, msg
  )
values (#{id,jdbcType=INTEGER}, #{domainName,jdbcType=VARCHAR}, #{taskName,jdbcType=VARCHAR},
  #{paramName,jdbcType=VARCHAR}, #{acquireType,jdbcType=INTEGER}, #{groupNum,jdbcType=INTEGER}, 
  #{acquireContent,jdbcType=VARCHAR}, #{version,jdbcType=VARCHAR}, #{enable,jdbcType=INTEGER}, #{msg,jdbcType=VARCHAR}
  )

mapper.select:
select

from param
where domain_name = #{domainName,jdbcType=VARCHAR}
and task_name = #{taskName,jdbcType=VARCHAR}
and param_name = #{paramName,jdbcType=VARCHAR}
and version = #{version,jdbcType=VARCHAR}
and enable = 1
