mapper.select:
select

from policy
where policy_name = 'default_policy'
and domain_name = #{domainName,jdbcType=VARCHAR}
and task_name = #{taskName,jdbcType=VARCHAR}
and version = #{version,jdbcType=VARCHAR}
and enable = 1

mapper.insert:
insert into policy (id, policy_name,  from_state, intent_names,
  to_state, domain_name, task_name, version, enable, msg
  )
values (#{id,jdbcType=INTEGER}, #{policyName,jdbcType=VARCHAR},  #{fromState,jdbcType=VARCHAR}, #{intentNames,jdbcType=VARCHAR},
  #{toState,jdbcType=VARCHAR}, #{domainName,jdbcType=VARCHAR}, #{taskName,jdbcType=VARCHAR},
  #{version,jdbcType=VARCHAR}, #{enable,jdbcType=TINYINT}, #{msg,jdbcType=VARCHAR}
  )

mapper.select:
select

from policy
where domain_name = #{domainName,jdbcType=VARCHAR}
and task_name = #{taskName,jdbcType=VARCHAR}
and from_state = #{fromState,jdbcType=VARCHAR}
and to_state = #{toState,jdbcType=VARCHAR}
and version = #{version,jdbcType=VARCHAR}
and enable = 1
