mapper.insert:
insert into transform_relation (id, transform_relation_name, domain_name, 
  task_name, context_state, intent_names, 
  transform_intent_name, enable, msg
  )
values (#{id,jdbcType=INTEGER}, #{transformRelationName,jdbcType=VARCHAR}, #{domainName,jdbcType=VARCHAR}, 
  #{taskName,jdbcType=VARCHAR}, #{contextState,jdbcType=VARCHAR}, #{intentNames,jdbcType=VARCHAR}, 
  #{transformIntentName,jdbcType=VARCHAR}, #{enable,jdbcType=TINYINT}, #{msg,jdbcType=VARCHAR}
  )

mapper.select:
select

from transform_relation
where enable = 1
and domain_name = #{domainName,jdbcType=VARCHAR}
and task_name = #{taskName,jdbcType=VARCHAR}
and (context_state = #{contextState,jdbcType=VARCHAR} or context_state = 'global_any')
